import org.apache.tools.ant.taskdefs.condition.Os
import com.google.protobuf.gradle.*

group = "dev.chords"
version = "0.1.0"

plugins {
    // Apply the java-library plugin for API and implementation separation.
    `java-library`

    // Protobuf plugin used to compile .proto sources
    id("com.google.protobuf") version "0.9.4"
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
    mavenLocal()
}

var grpcVersion = "1.68.0"

dependencies {
    // Use JUnit Jupiter for testing.
    testImplementation(libs.junit.jupiter)

    testRuntimeOnly("org.junit.platform:junit-platform-launcher")

    // This dependency is exported to consumers, that is to say found on their compile classpath.
    api(libs.commons.math3)

    // This dependency is used internally, and not exposed to consumers on their own compile classpath.
    implementation(libs.guava)

    implementation("dev.chords:choral-reactive")

    // ## gRPC ##
    runtimeOnly ("io.grpc:grpc-netty-shaded:${grpcVersion}")
    api("com.google.protobuf:protobuf-java:3.6.1")
    api ("io.grpc:grpc-protobuf:${grpcVersion}")
    api ("io.grpc:grpc-stub:${grpcVersion}")

    if (JavaVersion.current().isJava9Compatible()) {
        // Workaround for @javax.annotation.Generated
        // see: https://github.com/grpc/grpc-java/issues/3633
        implementation("javax.annotation:javax.annotation-api:1.3.1")
    }
}

var archSuffix = if (Os.isFamily(Os.FAMILY_MAC)) { ":osx-x86_64" } else { "" }

protobuf {
  protoc {
    // The artifact spec for the Protobuf Compiler
    artifact = "com.google.protobuf:protoc:3.6.1$archSuffix"
  }
  plugins {
    // Optional: an artifact spec for a protoc plugin, with "grpc" as
    // the identifier, which can be referred to in the "plugins"
    // container of the "generateProtoTasks" closure.
    id("grpc") {
      artifact = "io.grpc:protoc-gen-grpc-java:1.15.1$archSuffix"
    }
  }
  generateProtoTasks {
    ofSourceSet("main").forEach {
      it.plugins {
        // Apply the "grpc" plugin whose spec is defined above, without
        // options. Note the braces cannot be omitted, otherwise the
        // plugin will not be added. This is because of the implicit way
        // NamedDomainObjectContainer binds the methods.
        id("grpc") { }
      }
    }
  }
}

tasks.register("compileChoral") {
  val choreographies = listOf(
    "ChorAddCartItem",
    "ChorGetCartItems",
    "ChorPlaceOrder"
  )

  doLast {
    choreographies.forEach { name: String ->
      ProcessBuilder()
            .command(listOf(
              "choral", "epp",
              "--sources=./src/main/choral",
              "--headers=./src/main/choral",
              "--target=${buildDir}/generated/choral",
              name
            ))
            .directory(rootProject.projectDir)
            .start()
            .waitFor(60, TimeUnit.SECONDS)
    }
  }
}

tasks.build {
    dependsOn("compileChoral")
}

sourceSets {
   main {
      java {
         srcDir("${buildDir}/generated/choral")
      }
   }
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

tasks.named<Test>("test") {
    // Use JUnit Platform for unit tests.
    useJUnitPlatform()
}
